(24, 1, '2017-03-13 10:32:39', '2017-03-13 02:32:39', '<h6></h6>\r\n\r\n[code lang="js"]\r\n\r\n&amp;lt;pre&amp;gt;if (typeof jQuery === ''undefined'') {\r\n  throw new Error(''Bootstrap\\''s JavaScript requires jQuery'')\r\n}\r\n\r\n+function ($) {\r\n  ''use strict'';\r\n  var version = $.fn.jquery.split('' '')[0].split(''.'')\r\n  if ((version[0] &amp;lt; 2 &amp;amp;&amp;amp; version[1] &amp;lt; 9) || (version[0] == 1 &amp;amp;&amp;amp; version[1] == 9 &amp;amp;&amp;amp; version[2] &amp;lt; 1) || (version[0] &amp;gt; 3)) {\r\n    throw new Error(''Bootstrap\\''s JavaScript requires jQuery version 1.9.1 or higher, but lower than version 4'')\r\n  }\r\n}(jQuery);&amp;lt;/pre&amp;gt;\r\n\r\n[/code]\r\n', '哈哈哈', '', 'trash', 'open', 'closed', '', '%e5%93%88%e5%93%88%e5%93%88__trashed', '', '', '2017-03-21 17:10:40', '2017-03-21 09:10:40', '', 0, 'http://localhost/wordpress/?post_type=dwqa-question&#038;p=24', 0, 'dwqa-question', '', 0),
